import * as React from "react"
import { ChevronLeft, ChevronRight, MoreHorizontal } from "lucide-react"

import { cn } from "@/lib/utils"
import { ButtonProps, buttonVariants } from "@/components/ui/button"

const Pagination = ({ className, ...props }: React.ComponentProps<"nav">) => (
  <nav
    role="navigation"
    aria-label="pagination"
    className={cn("mx-auto flex w-full justify-center", className)}
    {...props}
  />
)
Pagination.displayName = "Pagination"

const PaginationContent = React.forwardRef<
  HTMLUListElement,
  React.ComponentProps<"ul">
>(({ className, ...props }, ref) => (
  <ul
    ref={ref}
    className={cn("flex flex-row items-center gap-1", className)}
    {...props}
  />
))
PaginationContent.displayName = "PaginationContent"

const PaginationItem = React.forwardRef<
  HTMLLIElement,
  React.ComponentProps<"li">
>(({ className, ...props }, ref) => (
  <li ref={ref} className={cn("", className)} {...props} />
))
PaginationItem.displayName = "PaginationItem"

type PaginationLinkProps = {
  isActive?: boolean
} & Pick<ButtonProps, "size"> &
  React.ComponentProps<"a">

const PaginationLink = ({
  className,
  isActive,
  size = "icon",
  ...props
}: PaginationLinkProps) => (
  <a
    aria-current={isActive ? "page" : undefined}
    className={cn(
      buttonVariants({
        variant: isActive ? "outline" : "ghost",
        size,
      }),
      className
    )}
    {...props}
  />
)
PaginationLink.displayName = "PaginationLink"

const PaginationPrevious = ({
  className,
  ...props
}: React.ComponentProps<typeof PaginationLink>) => (
  <PaginationLink
    aria-label="Go to previous page"
    size="default"
    className={cn("gap-1 pl-2.5", className)}
    {...props}
  >
    <ChevronLeft className="h-4 w-4" />
    <span>Previous</span>
  </PaginationLink>
)
PaginationPrevious.displayName = "PaginationPrevious"

const PaginationNext = ({
  className,
  ...props
}: React.ComponentProps<typeof PaginationLink>) => (
  <PaginationLink
    aria-label="Go to next page"
    size="default"
    className={cn("gap-1 pr-2.5", className)}
    {...props}
  >
    <span>Next</span>
    <ChevronRight className="h-4 w-4" />
  </PaginationLink>
)
PaginationNext.displayName = "PaginationNext"

const PaginationEllipsis = ({
  className,
  ...props
}: React.ComponentProps<"span">) => (
  <span
    aria-hidden
    className={cn("flex h-9 w-9 items-center justify-center", className)}
    {...props}
  >
    <MoreHorizontal className="h-4 w-4" />
    <span className="sr-only">More pages</span>
  </span>
)
PaginationEllipsis.displayName = "PaginationEllipsis"

export {
  Pagination,
  PaginationContent,
  PaginationEllipsis,
  PaginationItem,
  PaginationLink,
  PaginationNext,
  PaginationPrevious,
}

client/src/components/FilterChips.tsx.content:
import { Badge } from "@/components/ui/badge";
import { X, ChevronDown } from "lucide-react";
import { useState } from "react";
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from "@/components/ui/collapsible";

export interface Filters {
  traits: string[];
  priceRange: string | null;
  sizes: string[];
}

interface FilterChipsProps {
  filters: Filters;
  onFilterChange: (filters: Filters) => void;
}

const traitOptions = [
  { value: "vibrant", label: "Vibrant" },
  { value: "free-flying", label: "Free Flying" },
  { value: "talking", label: "Talking" },
  { value: "intelligent", label: "Intelligent" },
  { value: "human-bonding", label: "Human Bonding" },
];

const sizeOptions = [
  { value: "small", label: "Small" },
  { value: "medium", label: "Medium" },
  { value: "large", label: "Large" },
];

const priceRangeOptions = [
  { label: "Under ‚Çπ1L", value: "0-100000" },
  { label: "‚Çπ1L - ‚Çπ2L", value: "100000-200000" },
  { label: "‚Çπ2L - ‚Çπ3L", value: "200000-300000" },
  { label: "Above ‚Çπ3L", value: "300000-999999999" },
];

export default function FilterChips({ filters, onFilterChange }: FilterChipsProps) {
  const [openSection, setOpenSection] = useState<string | null>(null);

  const handleSectionToggle = (section: string) => {
    setOpenSection(openSection === section ? null : section);
  };

  const toggleTrait = (trait: string) => {
    const newTraits = filters.traits.includes(trait)
      ? filters.traits.filter(t => t !== trait)
      : [...filters.traits, trait];
    onFilterChange({ ...filters, traits: newTraits });
  };

  const toggleSize = (size: string) => {
    const newSizes = filters.sizes.includes(size)
      ? filters.sizes.filter(s => s !== size)
      : [...filters.sizes, size];
    onFilterChange({ ...filters, sizes: newSizes });
  };

  const togglePriceRange = (range: string) => {
    onFilterChange({ 
      ...filters, 
      priceRange: filters.priceRange === range ? null : range 
    });
  };

  const clearAll = () => {
    onFilterChange({ traits: [], priceRange: null, sizes: [] });
  };

  const hasActiveFilters = filters.traits.length > 0 || filters.priceRange !== null || filters.sizes.length > 0;

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-xl font-bold text-foreground">Filter Birds</h3>
        {hasActiveFilters && (
          <button
            onClick={clearAll}
            className="text-sm text-primary hover:text-primary/80 font-medium transition-colors flex items-center gap-1"
            data-testid="button-clear-filters"
          >
            <X className="h-4 w-4" />
            Clear all
          </button>
        )}
      </div>

      <div className="space-y-4">
        <Collapsible open={openSection === 'traits'} onOpenChange={() => handleSectionToggle('traits')} className="border-b pb-4">
          <CollapsibleTrigger className="flex items-center justify-between w-full hover-elevate p-2 rounded-md" data-testid="toggle-traits">
            <p className="text-sm font-semibold text-foreground uppercase tracking-wide">Bird Traits</p>
            <ChevronDown className={`h-4 w-4 text-muted-foreground transition-transform ${openSection === 'traits' ? 'rotate-180' : ''}`} />
          </CollapsibleTrigger>
          <CollapsibleContent className="pt-3">
            <div className="flex flex-col gap-2">
              {traitOptions.map((trait) => (
                <label
                  key={trait.value}
                  className="flex items-center gap-2 cursor-pointer hover-elevate p-2 rounded-md transition-colors"
                >
                  <input
                    type="checkbox"
                    checked={filters.traits.includes(trait.value)}
                    onChange={() => toggleTrait(trait.value)}
                    className="w-4 h-4 text-primary rounded border-gray-300 focus:ring-primary"
                    data-testid={`filter-trait-${trait.value}`}
                  />
                  <span className="text-sm text-foreground">{trait.label}</span>
                </label>
              ))}
            </div>
          </CollapsibleContent>
        </Collapsible>

        <Collapsible open={openSection === 'size'} onOpenChange={() => handleSectionToggle('size')} className="border-b pb-4">
          <CollapsibleTrigger className="flex items-center justify-between w-full hover-elevate p-2 rounded-md" data-testid="toggle-size">
            <p className="text-sm font-semibold text-foreground uppercase tracking-wide">Size</p>
            <ChevronDown className={`h-4 w-4 text-muted-foreground transition-transform ${openSection === 'size' ? 'rotate-180' : ''}`} />
          </CollapsibleTrigger>
          <CollapsibleContent className="pt-3">
            <div className="flex flex-col gap-2">
              {sizeOptions.map((size) => (
                <label
                  key={size.value}
                  className="flex items-center gap-2 cursor-pointer hover-elevate p-2 rounded-md transition-colors"
                >
                  <input
                    type="checkbox"
                    checked={filters.sizes.includes(size.value)}
                    onChange={() => toggleSize(size.value)}
                    className="w-4 h-4 text-primary rounded border-gray-300 focus:ring-primary"
                    data-testid={`filter-size-${size.value}`}
                  />
                  <span className="text-sm text-foreground">{size.label}</span>
                </label>
              ))}
            </div>
          </CollapsibleContent>
        </Collapsible>

        <Collapsible open={openSection === 'price'} onOpenChange={() => handleSectionToggle('price')} className="pt-2">
          <CollapsibleTrigger className="flex items-center justify-between w-full hover-elevate p-2 rounded-md" data-testid="toggle-price">
            <p className="text-sm font-semibold text-foreground uppercase tracking-wide">Price Range</p>
            <ChevronDown className={`h-4 w-4 text-muted-foreground transition-transform ${openSection === 'price' ? 'rotate-180' : ''}`} />
          </CollapsibleTrigger>
          <CollapsibleContent className="pt-3">
            <div className="flex flex-col gap-2">
              {priceRangeOptions.map((range) => (
                <label
                  key={range.value}
                  className="flex items-center gap-2 cursor-pointer hover-elevate p-2 rounded-md transition-colors"
                >
                  <input
                    type="radio"
                    checked={filters.priceRange === range.value}
                    onChange={() => togglePriceRange(range.value)}
                    className="w-4 h-4 text-primary border-gray-300 focus:ring-primary"
                    data-testid={`filter-price-${range.value}`}
                  />
                  <span className="text-sm text-foreground">{range.label}</span>
                </label>
              ))}
            </div>
          </CollapsibleContent>
        </Collapsible>

        {hasActiveFilters && (
          <div className="pt-4 border-t">
            <p className="text-xs font-medium text-muted-foreground mb-2">Active Filters:</p>
            <div className="flex flex-wrap gap-2">
              {filters.traits.map((trait) => (
                <Badge key={trait} variant="default" className="text-xs">
                  {traitOptions.find(t => t.value === trait)?.label}
                  <button
                    onClick={() => toggleTrait(trait)}
                    className="ml-1 hover:text-destructive"
                  >
                    <X className="h-3 w-3" />
                  </button>
                </Badge>
              ))}
              {filters.sizes.map((size) => (
                <Badge key={size} variant="default" className="text-xs">
                  {sizeOptions.find(s => s.value === size)?.label}
                  <button
                    onClick={() => toggleSize(size)}
                    className="ml-1 hover:text-destructive"
                  >
                    <X className="h-3 w-3" />
                  </button>
                </Badge>
              ))}
              {filters.priceRange && (
                <Badge variant="default" className="text-xs">
                  {priceRangeOptions.find(r => r.value === filters.priceRange)?.label}
                  <button
                    onClick={() => togglePriceRange(filters.priceRange!)}
                    className="ml-1 hover:text-destructive"
                  >
                    <X className="h-3 w-3" />
                  </button>
                </Badge>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/components/BirdShowcase.tsx.content:
import { useState, useEffect } from "react";
import { Badge } from "@/components/ui/badge";
import { IndianRupee, Ruler, Pause, Play } from "lucide-react";
import { Button } from "@/components/ui/button";
import type { Bird } from "@shared/schema";

interface BirdShowcaseProps {
  birds: Bird[];
}

export default function BirdShowcase({ birds }: BirdShowcaseProps) {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isPaused, setIsPaused] = useState(false);
  const [isHovered, setIsHovered] = useState(false);
  const currentBird = birds[currentIndex];

  useEffect(() => {
    if (isPaused || isHovered) return;

    const interval = setInterval(() => {
      setCurrentIndex((prev) => (prev + 1) % birds.length);
    }, 3000);

    return () => clearInterval(interval);
  }, [birds.length, isPaused, isHovered]);

  if (!currentBird) return null;

  const formatPrice = (min: number, max: number) => {
    const formatNum = (num: number) => {
      if (num >= 100000) {
        return `${(num / 100000).toFixed(1)}L`;
      }
      return `${(num / 1000).toFixed(0)}K`;
    };
    return `‚Çπ${formatNum(min)}-${formatNum(max)}`;
  };

  const formatSize = (size: string) => {
    return size.charAt(0).toUpperCase() + size.slice(1);
  };

  const formatTrait = (trait: string) => {
    return trait.split('-').map(word => 
      word.charAt(0).toUpperCase() + word.slice(1)
    ).join(' ');
  };

  return (
    <div className="relative w-full max-w-md mx-auto md:mx-0" data-testid="bird-showcase">
      <div 
        className="relative aspect-square rounded-lg overflow-hidden bg-muted"
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
        onFocus={() => setIsHovered(true)}
        onBlur={() => setIsHovered(false)}
      >
        <img
          src={currentBird.image}
          alt={currentBird.name}
          className="w-full h-full object-cover transition-opacity duration-500"
          data-testid="showcase-bird-image"
        />
        <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-black/20 to-transparent" />

        <Button
          size="icon"
          variant="secondary"
          className="absolute top-4 right-4 bg-white/90 hover:bg-white z-10"
          onClick={() => setIsPaused(!isPaused)}
          aria-label={isPaused ? "Resume slideshow" : "Pause slideshow"}
          data-testid="showcase-pause-button"
        >
          {isPaused ? <Play className="h-4 w-4" /> : <Pause className="h-4 w-4" />}
        </Button>

        <div className="absolute bottom-0 left-0 right-0 p-4 text-white">
          <h3 className="text-xl font-bold mb-3" data-testid="showcase-bird-name">
            {currentBird.name}
          </h3>

          <div className="flex items-center gap-2 text-sm flex-wrap">
            <Badge 
              variant="secondary" 
              className="bg-white/90 text-foreground hover:bg-white/90 no-default-hover-elevate no-default-active-elevate"
              data-testid="showcase-trait"
            >
              {formatTrait(currentBird.traits[0])}
            </Badge>

            <span className="text-white/80">|</span>

            <Badge 
              variant="secondary" 
              className="bg-white/90 text-foreground hover:bg-white/90 no-default-hover-elevate no-default-active-elevate flex items-center gap-1"
              data-testid="showcase-price"
            >
              <IndianRupee className="h-3 w-3" />
              {formatPrice(currentBird.priceMin, currentBird.priceMax)}
            </Badge>

            <span className="text-white/80">|</span>

            <Badge 
              variant="secondary" 
              className="bg-white/90 text-foreground hover:bg-white/90 no-default-hover-elevate no-default-active-elevate flex items-center gap-1"
              data-testid="showcase-size"
            >
              <Ruler className="h-3 w-3" />
              {formatSize(currentBird.size)}
            </Badge>
          </div>

          <div className="flex justify-center gap-1.5 mt-3" role="tablist" aria-label="Bird selection">
            {birds.map((_, index) => (
              <button
                key={index}
                role="tab"
                onClick={() => setCurrentIndex(index)}
                className={`h-1.5 rounded-full transition-all ${
                  index === currentIndex 
                    ? 'bg-white w-6' 
                    : 'bg-white/50 w-1.5 hover:bg-white/70'
                }`}
                data-testid={`showcase-indicator-${index}`}
                aria-label={`View ${birds[index].name}`}
                aria-selected={index === currentIndex}
                aria-current={index === currentIndex ? "true" : undefined}
              />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/DoctorShowcase.tsx.content:
import { useState, useEffect } from "react";
import { Badge } from "@/components/ui/badge";
import { Card } from "@/components/ui/card";
import { Pause, Play } from "lucide-react";
import { Button } from "@/components/ui/button";

interface SubscriptionPlan {
  name: string;
  price: string;
  consultations: string;
  savings?: string;
}

interface DoctorInfo {
  name: string;
  image: string;
  credentials: string[];
  specialization: string;
}

interface Props {
  doctor: DoctorInfo;
  subscriptionPlans: SubscriptionPlan[];
}

export default function DoctorShowcase({ doctor, subscriptionPlans }: Props) {
  const [currentPlanIndex, setCurrentPlanIndex] = useState(0);
  const [isPaused, setIsPaused] = useState(false);
  const [isHovered, setIsHovered] = useState(false);

  const currentPlan = subscriptionPlans[currentPlanIndex];

  useEffect(() => {
    if (isPaused || isHovered || subscriptionPlans.length <= 1) return;

    const interval = setInterval(() => {
      setCurrentPlanIndex((prev) => (prev + 1) % subscriptionPlans.length);
    }, 3000);

    return () => clearInterval(interval);
  }, [isPaused, isHovered, subscriptionPlans.length]);

  return (
    <Card
      className="relative overflow-hidden max-w-md w-full"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      onFocus={() => setIsHovered(true)}
      onBlur={() => setIsHovered(false)}
      tabIndex={0}
      data-testid="doctor-showcase"
    >
      <div className="relative">
        <img
          src={doctor.image}
          alt={doctor.name}
          className="w-full h-64 object-cover"
          data-testid="doctor-image"
        />

        {subscriptionPlans.length > 1 && (
          <Button
            size="icon"
            variant="secondary"
            className="absolute top-4 right-4 h-8 w-8 bg-background/80 backdrop-blur-sm hover:bg-background/90"
            onClick={() => setIsPaused(!isPaused)}
            aria-label={isPaused ? "Resume slideshow" : "Pause slideshow"}
            data-testid="showcase-pause-button"
          >
            {isPaused ? (
              <Play className="h-4 w-4" />
            ) : (
              <Pause className="h-4 w-4" />
            )}
          </Button>
        )}
      </div>

      <div className="p-6">
        <h3 className="text-xl font-bold text-foreground mb-2" data-testid="doctor-name">
          {doctor.name}
        </h3>

        <p className="text-sm text-muted-foreground mb-3" data-testid="doctor-specialization">
          {doctor.specialization}
        </p>

        <div className="flex flex-wrap gap-2 mb-4">
          {doctor.credentials.map((credential, idx) => (
            <Badge
              key={idx}
              variant="secondary"
              className="text-xs"
              data-testid={`credential-${idx}`}
            >
              {credential}
            </Badge>
          ))}
        </div>

        <div className="border-t pt-4">
          <div className="flex items-baseline justify-between mb-2">
            <h4 className="text-lg font-semibold text-foreground" data-testid="plan-name">
              {currentPlan.name}
            </h4>
            <span className="text-2xl font-bold text-primary" data-testid="plan-price">
              {currentPlan.price}
            </span>
          </div>

          <div className="flex items-center gap-2 mb-3">
            <Badge variant="outline" className="text-xs" data-testid="plan-consultations">
              {currentPlan.consultations}
            </Badge>
            {currentPlan.savings && (
              <Badge className="text-xs bg-green-600 hover:bg-green-700" data-testid="plan-savings">
                {currentPlan.savings}
              </Badge>
            )}
          </div>

          {subscriptionPlans.length > 1 && (
            <div
              className="flex justify-center gap-2 mt-4"
              role="tablist"
              aria-label="Subscription plan selection"
            >
              {subscriptionPlans.map((_, index) => (
                <button
                  key={index}
                  onClick={() => setCurrentPlanIndex(index)}
                  className={`h-2 rounded-full transition-all ${
                    index === currentPlanIndex
                      ? "w-8 bg-primary"
                      : "w-2 bg-muted-foreground/30"
                  }`}
                  aria-label={`Select ${subscriptionPlans[index].name}`}
                  aria-current={index === currentPlanIndex ? "true" : undefined}
                  role="tab"
                  data-testid={`plan-indicator-${index}`}
                />
              ))}
            </div>
          )}
        </div>
      </div>
    </Card>
  );
}

